import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isError, setIsError] = useState("");
  const [isSuccess, setIsSuccess] = useState("");

  // ðŸ”¹ Handle Login
  async function handleLogin() {
    if (!username || !password) {
      setIsError("Please enter username and password");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8080/users/login",
        { username, password },
        { headers: { "Content-Type": "application/json" } }
      );

      if (response.status === 200 && response.data.token) {
        sessionStorage.setItem("token", response.data.token);
        setIsError("");
        setIsSuccess("Login successful!");
        navigate("/table"); // Redirect after login
      }
    } catch (error) {
      console.error(error);
      setIsError(
        error.response?.data?.error || "The username or password is wrong!"
      );
      setIsSuccess("");
    } finally {
      setTimeout(() => {
        setIsError("");
        setIsSuccess("");
      }, 3000);
      setUsername("");
      setPassword("");
    }
  }

  // ðŸ”¹ Handle Registration
  async function handleRegister() {
    if (!username || !password) {
      setIsError("Please enter username and password");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8080/users/register",
        { username, password },
        { headers: { "Content-Type": "application/json" } }
      );

      if (response.status === 200) {
        setIsSuccess(response.data.message);
        setIsError("");
      }
    } catch (error) {
      console.error(error);
      setIsError(
        error.response?.data?.error || "Registration failed. Try another username."
      );
      setIsSuccess("");
    } finally {
      setTimeout(() => {
        setIsError("");
        setIsSuccess("");
      }, 3000);
      setUsername("");
      setPassword("");
    }
  }

  return (
    <div className="container">
      <h1>LIBRARY HOME PAGE</h1>
      <label htmlFor="username">Username:</label>
      <input
        type="text"
        name="username"
        value={username}
        onChange={(e) => setUsername(e.target.value.trim())}
      />
      <br />
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        name="password"
        value={password}
        onChange={(e) => setPassword(e.target.value.trim())}
      />
      <div className="button-container">
        <button className="button login" onClick={handleLogin}>
          Login
        </button>
        <button className="button register" onClick={handleRegister}>
          Register
        </button>
      </div>
      {isError && <p className="error">{isError}</p>}
      {isSuccess && <p className="success">{isSuccess}</p>}
    </div>
  );
}

export default Login;
