import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isError, setIsError] = useState("");
  const [isSuccess, setIsSuccess] = useState("");

  async function handleLogin() {
    if (!username || !password) {
      setIsError("Username and password are required!");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8080/users/login",
        { username, password },
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      if (response.status === 200) {
        // backend sends { token: "..." }
        const token = response.data.token;
        sessionStorage.setItem("token", token);
        setIsError("");
        setIsSuccess("Login successful! Redirecting...");
        setTimeout(() => navigate("/table"), 1000);
      }
    } catch (error) {
      if (error.response) {
        // 400, 403, etc
        setIsError(error.response.data || "Login failed. Check credentials.");
      } else {
        setIsError("Server not reachable.");
      }
      setIsSuccess("");
    }

    setUsername("");
    setPassword("");
    setTimeout(() => {
      setIsError("");
      setIsSuccess("");
    }, 3000);
  }

  async function handleRegister() {
    if (!username || !password) {
      setIsError("Username and password are required!");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8080/users/register",
        { username, password },
        {
          headers: { "Content-Type": "application/json" },
        }
      );

      if (response.status === 200) {
        setIsError("");
        setIsSuccess("Registered successfully! Please log in.");
      }
    } catch (error) {
      if (error.response) {
        setIsError(error.response.data || "Registration failed.");
      } else {
        setIsError("Server not reachable.");
      }
      setIsSuccess("");
    }

    setUsername("");
    setPassword("");
    setTimeout(() => {
      setIsError("");
      setIsSuccess("");
    }, 3000);
  }

  return (
    <div className="container">
      <h1>LIBRARY HOME PAGE</h1>
      <label htmlFor="username">Username:</label>
      <input
        type="text"
        name="username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <br />
      <label htmlFor="password">Password:</label>
      <input
        type="password"
        name="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <div className="button-container">
        <button className="button login" onClick={handleLogin}>
          Login
        </button>
        <button className="button register" onClick={handleRegister}>
          Register
        </button>
      </div>
      {isError && <p className="error">{isError}</p>}
      {isSuccess && <p className="success">{isSuccess}</p>}
    </div>
  );
}

export default Login;
