import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isError, setIsError] = useState("");
  const [isSuccess, setIsSuccess] = useState("");

  const handleLogin = async () => {
    if (!username || !password) {
      setIsError("Please enter both username and password");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8080/users/login",
        { username, password },
        { headers: { "Content-Type": "application/json" } }
      );

      sessionStorage.setItem("token", response.data);
      setIsError("");
      setIsSuccess("Login successful!");
      navigate("/table");
    } catch (error) {
      console.error(error);
      setIsError(error.response?.data || "Login failed");
      setIsSuccess("");
    }

    setUsername("");
    setPassword("");
    setTimeout(() => {
      setIsError("");
      setIsSuccess("");
    }, 3000);
  };

  const handleRegister = async () => {
    if (!username || !password) {
      setIsError("Please enter both username and password");
      return;
    }

    try {
      const response = await axios.post(
        "http://localhost:8080/users/register",
        { username, password },
        { headers: { "Content-Type": "application/json" } }
      );

      setIsSuccess(response.data);
      setIsError("");
    } catch (error) {
      console.error(error);
      setIsError(error.response?.data || "Registration failed");
      setIsSuccess("");
    }

    setUsername("");
    setPassword("");
    setTimeout(() => {
      setIsError("");
      setIsSuccess("");
    }, 3000);
  };

  return (
    <div className="container">
      <h1>LIBRARY HOME PAGE</h1>

      <label htmlFor="username">Username:</label>
      <input
        type="text"
        name="username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <br />

      <label htmlFor="password">Password:</label>
      <input
        type="password"
        name="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />

      <div className="button-container">
        <button onClick={handleLogin}>Login</button>
        <button onClick={handleRegister}>Register</button>
      </div>

      {isError && <p className="error">{isError}</p>}
      {isSuccess && <p className="success">{isSuccess}</p>}
    </div>
  );
}

export default Login;
