import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

function Login() {
const navigate = useNavigate();
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");
const [isError, setIsError] = useState("");
const [isSuccess, setIsSuccess] = useState("");

async function handleLogin() {
if (!username || !password) {
setIsError("Please enter both username and password");
return;
}

```
try {
  const response = await axios.post(
    "http://localhost:8080/users/login",
    { username: username.trim(), password: password.trim() },
    { headers: { "Content-Type": "application/json" } }
  );

  sessionStorage.setItem("token", response.data);
  setIsError("");
  setIsSuccess("Login successful!");
  navigate("/table");
} catch (error) {
  console.error(error);
  setIsError(
    error.response?.data || "Invalid username or password."
  );
  setIsSuccess("");
} finally {
  setTimeout(() => {
    setIsError("");
    setIsSuccess("");
  }, 3000);
  setUsername("");
  setPassword("");
}
```

}

async function handleRegister() {
if (!username || !password) {
setIsError("Please enter both username and password");
return;
}

```
try {
  const response = await axios.post(
    "http://localhost:8080/users/register",
    { username: username.trim(), password: password.trim() },
    { headers: { "Content-Type": "application/json" } }
  );

  setIsSuccess("You are registered! Please log in.");
  setIsError("");
} catch (error) {
  console.error(error);
  setIsError(
    error.response?.data || "Registration failed. Username may already exist."
  );
  setIsSuccess("");
} finally {
  setTimeout(() => {
    setIsError("");
    setIsSuccess("");
  }, 3000);
  setUsername("");
  setPassword("");
}
```

}

return ( <div className="container"> <h1>LIBRARY HOME PAGE</h1> <label htmlFor="username">Username:</label>
<input
type="text"
name="username"
value={username}
onChange={(e) => setUsername(e.target.value)}
/> <br /> <label htmlFor="password">Password:</label>
<input
type="password"
name="password"
value={password}
onChange={(e) => setPassword(e.target.value)}
/> <div className="button-container"> <button onClick={handleLogin}>Login</button> <button onClick={handleRegister}>Register</button> </div>
{isError && <p className="error">{isError}</p>}
{isSuccess && <p className="success">{isSuccess}</p>} </div>
);
}

export default Login;
