import { useNavigate } from "react-router-dom";
import { useState } from "react";
import axios from "axios";

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [isError, setIsError] = useState("");
  const [isSuccess, setIsSuccess] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault(); // prevent form reload
    if (username && password) {
      try {
        const response = await axios.post(
          "http://localhost:8080/users/login",
          { username: username.trim(), password: password.trim() },
          { headers: { "Content-Type": "application/json" } }
        );

        if (response.status === 200) {
          const token = response.data;
          sessionStorage.setItem("token", token);
          navigate("/table");
        }
      } catch (error) {
        setIsError(error.response?.data || "Login failed");
        console.error(error);
        setTimeout(() => setIsError(""), 3000);
      }
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    if (username && password) {
      try {
        const response = await axios.post(
          "http://localhost:8080/users/register",
          { username: username.trim(), password: password.trim() },
          { headers: { "Content-Type": "application/json" } }
        );

        if (response.status === 200) {
          setIsSuccess("Registered successfully! Please log in.");
        }
      } catch (error) {
        setIsError(error.response?.data || "Registration failed");
      } finally {
        setTimeout(() => {
          setIsError("");
          setIsSuccess("");
        }, 3000);
      }
    }
  };

  return (
    <div className="container">
      <h1>LIBRARY HOME PAGE</h1>
      <form>
        <label>Username:</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <br />
        <label>Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <div className="button-container">
          <button type="button" onClick={handleLogin}>Login</button>
          <button type="button" onClick={handleRegister}>Register</button>
        </div>
      </form>
      {isError && <p className="error">{isError}</p>}
      {isSuccess && <p className="success">{isSuccess}</p>}
    </div>
  );
}

export default Login;
